
#brute force
def flipbulb (target, pos):
    i = pos
    res = []
    for char in target:
        res.append(char)
    while i < len(res):
        if res[i] == '0':
            res[i] = '1'
        else:
            res[i] = '0'
        i += 1
    return ''.join(res)

target = ""
starting = '0' * len(target)
counter = 0

left = 0
right = 0
while right < len(target):
    if starting[left] != target[right]:
        starting = flipbulb(starting,left)
        left += 1
        right += 1
        counter += 1

    else:
        left += 1
        right += 1

print(counter)

